import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';

class Home extends Component {
    api_key = "gKqNNLdx3R6yG0GiFbOOAScG4u6SKbJK";
    card_index = 0

    constructor(props) {
        super(props);
        this.createBookCard = this.createBookCard.bind(this);
        this.state = {
            books: []
        };
    }

    componentDidMount() {
        var allBooks = []
        fetch(`https://api.nytimes.com/svc/books/v3/lists/names.json?api-key=${this.api_key}`)
            .then(response => response.json())
            .then(data1 => {
                // data1.results.forEach(element => {
                //     fetch(`https://api.nytimes.com/svc/books/v3/lists/current/${element.list_name_encoded}.json?api-key=${this.api_key}`)
                //         .then(response => response.json())
                //         .then(data2 => data2.element.book.array.forEach(element => {
                //             allBooks.push(element);
                //         }));
                // })
                fetch(`https://api.nytimes.com/svc/books/v3/lists/current/${data1.results[0].list_name_encoded}.json?api-key=${this.api_key}`)
                    .then(response => response.json())
                    .then(data2 => data2.results.books.forEach(element => allBooks.push(element)))
                    .then(_ => {this.setState({books: allBooks})});     
            })
    }

    componentDidUpdate() {
        this.card_index = 0;
    }

    createBookCard(coverPic, title = "", description = "A best selling book", author = "", publisher = "", isbn = "None") {
        var to_next_row = this.card_index % 3 === 0;
        this.card_index += 1;
        if (!to_next_row) {
            return (
            <div className="col">
                <div className="card" style={{width: "18rem"}}>
                    <img className="card-img-top" src={coverPic} alt="Card cap"/>
                    <div className="card-body">
                        <h5 className="card-title">{title}</h5>
                        <p className="card-text">{description}</p>
                    </div>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item"> <b>Author:</b> &nbsp; {author} </li>
                        <li className="list-group-item"> <b>Publisher:</b> &nbsp; {publisher} </li>
                        <li className="list-group-item"> <b>ISBN:</b> &nbsp; {isbn} </li>
                    </ul>
                </div>
            </div>);
        } else {
            return (
                <div className="row">
                <div className="col">
                    <div className="card" style={{width: "18rem"}}>
                        <img className="card-img-top" src={coverPic} alt="Card cap"/>
                        <div className="card-body">
                            <h5 className="card-title">{title}</h5>
                            <p className="card-text">{description}</p>
                        </div>
                        <ul className="list-group list-group-flush">
                            <li className="list-group-item"> <b>Author:</b> &nbsp; {author} </li>
                            <li className="list-group-item"> <b>Publisher:</b> &nbsp; {publisher} </li>
                            <li className="list-group-item"> <b>ISBN:</b> &nbsp; {isbn} </li>
                        </ul>
                    </div>
                </div>
                </div>);
        }
    }

    render() {
        return <div className="container"> 
            {this.state.books.map(book => this.createBookCard(book.book_image, book.title, 
                book.description, book.author, book.publisher, book.isbns.isbn10))} 
        </div>;
    }

}

export default Home;
