import React, { Component } from 'react';
import { Navbar, Nav, NavItem, NavDropdown, MenuItem } from 'react-bootstrap/Navbar';

class Home extends Component {
    api_key = "gKqNNLdx3R6yG0GiFbOOAScG4u6SKbJK";

    constructor(props) {
        super(props);
        this.state = {
            bookCategories: []
        };
    }

    componentDidMount() {
        fetch(`https://api.nytimes.com/svc/books/v3/lists/names.json?api-key=${this.api_key}`)
            .then(response => response.json())
            .then(data => this.setState({bookCategories: data.results}));
    }

    render() {
        return (
            <Navbar className="navbar navbar-expand-lg navbar-light bg-light">
            <a className="navbar-brand" href="#">NYTimes Bestsellers</a>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNavDropdown">
                <ul className="navbar-nav">
                    <li className="nav-item dropdown">
                        <a className="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                        Categories
                        </a>
                        <div className="dropdown-menu" aria-labelledby="navbarDropdownMenuLink">
                            { this.state.bookCategories.map((element, index) => {
                                if (index === 0) {
                                    return <li className="nav-item active">
                                        <a className="dropdown-item" href="#"> {element.list_name} </a>
                                    </li>;
                                } else {
                                    return <li className="nav-item">
                                        <a className="dropdown-item" href="#" list_name_encoded={element.list_name_encoded}> { element.list_name } </a>
                                    </li>
                                }
                            })}
                        </div>
                    </li>
                </ul>
            </div>
            </Navbar>
        );
    }
}

export default Home;
