import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';

class Home extends Component {
    api_key = "gKqNNLdx3R6yG0GiFbOOAScG4u6SKbJK";

    constructor(props) {
        super(props);
        this.createBookCard = this.createBookCard.bind(this);
        this.state = {
            books: []
        };
    }

    componentDidUpdate() {
        var allBooks = []
        fetch(`https://api.nytimes.com/svc/books/v3/lists/names.json?api-key=${this.api_key}`)
            .then(response => response.json())
            .then(data1 => {
                // data1.results.forEach(element => {
                //     fetch(`https://api.nytimes.com/svc/books/v3/lists/current/${element.list_name_encoded}.json?api-key=${this.api_key}`)
                //         .then(response => response.json())
                //         .then(data2 => data2.element.book.array.forEach(element => {
                //             allBooks.push(element);
                //         }));
                // })
                fetch(`https://api.nytimes.com/svc/books/v3/lists/current/${data1.results[0].list_name_encoded}.json?api-key=${this.api_key}`)
                    .then(response => response.json())
                    .then(data2 => {
                        console.log(data2)
                        data2.results.books.forEach(element => allBooks.push(element))
                    })
                    .then(_ => {this.setState({books: allBooks}, () => console.log(this.state.books));})
            });
    }

    createBookCard(title = "", description = "", author = "", publishDate = "", isbn = "") {
        return (<div class="card" style={{width: "18rem"}}>
            <img class="card-img-top" src="..." alt="Card cap"/>
            <div class="card-body">
                <h5 class="card-title">{title}</h5>
                <p class="card-text">{description}</p>
            </div>
            <ul class="list-group list-group-flush">
                <li class="list-group-item"> <b>Author:</b>{author} </li>
                <li class="list-group-item"> <b>Publication Date:</b> {publishDate} </li>
                <li class="list-group-item"> <b>ISBN:</b> {isbn} </li>
            </ul>
        </div>);
    }

    render() {
        return <> {this.state.books.forEach(book => {this.createBookCard()})} </>;
    }

}

export default Home;
