import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';

class Home extends Component {
    api_key = "gKqNNLdx3R6yG0GiFbOOAScG4u6SKbJK";

    constructor(props) {
        super(props);
        this.createBookCard = this.createBookCard.bind(this);
        this.state = {
            books: []
        };
    }

    componentDidMount() {
        var allBooks = []
        fetch(`https://api.nytimes.com/svc/books/v3/lists/names.json?api-key=${this.api_key}`)
            .then(response => response.json())
            .then(data1 => {
                // data1.results.forEach(element => {
                //     fetch(`https://api.nytimes.com/svc/books/v3/lists/current/${element.list_name_encoded}.json?api-key=${this.api_key}`)
                //         .then(response => response.json())
                //         .then(data2 => data2.element.book.array.forEach(element => {
                //             allBooks.push(element);
                //         }));
                // })
                fetch(`https://api.nytimes.com/svc/books/v3/lists/current/${data1.results[0].list_name_encoded}.json?api-key=${this.api_key}`)
                    .then(response => response.json())
                    .then(data2 => data2.results.books.forEach(element => allBooks.push(element)))
                    .then(_ => {this.setState({books: allBooks})});     
            })
    }

    createBookCard(index = 0, coverPic, title = "", description = "A best selling book", author = "", publisher = "", isbn = "None") {
        return (
        <div className="col">
            <div className="card" style={{width: "18rem"}}>
                <img className="card-img-top" src={coverPic} alt="Card cap"/>
                <div className="card-body" style={{height:"30rem"}}>
                    <h5 className="card-title">{title}</h5>
                    <p className="card-text">{description}</p>
                </div>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item"> <b>Author:</b> &nbsp; {author} </li>
                    <li className="list-group-item"> <b>Publisher:</b> &nbsp; {publisher} </li>
                    <li className="list-group-item"> <b>ISBN:</b> &nbsp; {isbn} </li>
                </ul>
                <div class="card-body">
                <button type="button" className="btn btn-primary card-link">Edit</button>
                <button type="button" className="btn btn-danger card-link">Delete</button>
                </div>
            </div>
        </div>);
    }

    selectNextThree(index, nextBooks) {
        var cards = new Array(3);
        const j = index;
        for (; index < j + 3 && index < this.state.books.length; index++) {
            const book = nextBooks[index % 3];
            cards[index % 3] = this.createBookCard(index, book.book_image, book.title, book.description, 
                    book.author, book.publisher, book.isbns.isbn10);
        }
        return <div className="row" style={{margin:"30px 0px 30px 0px"}}> {cards[0]} {cards[1]} {cards[2]} </div>;
    }

    render() {
        var nextBooks = new Array(3);
        return <div className="container"> 
            {this.state.books.map((book, index) => {
                    nextBooks[index % 3] = book;
                    if ((index === this.state.books.length || index !== 0) && index % 3 === 0) { 
                        return this.selectNextThree(index, nextBooks);
                    } else {
                        return <> </>;
                    }
                })}
        </div>;
    }

}

export default Home;
